from Crypto.Util.number import *
from secret import FLAG

if __name__ == '__main__':
    flag = bytes_to_long(FLAG.encode())
    n = getPrime(1024) * getPrime(1024)
    e1 = getPrime(201)
    e2 = getPrime(201)
    print(f'n = {n}')
    print(f'e1 = {e1}')
    print(f'e2 = {e2}')
    print(f'c1 = {pow(flag, e1, n)}')
    print(f'c2 = {pow(flag, e2, n)}')

